import React, {useState} from 'react';
import './App.css';
import Card from './components/Card';
import Home from './components/Home';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';

function App() {

  const getAnimals = (cant) => {
    let animalList = [];
    while(animalList.length<cant){
      const num = getRandomInt(0,aniMax);
      setAniMax(aniMax-1);

      const selectedAnimal = animals[num];
      animalList.push(selectedAnimal);

      let filteredAnimals = animals.filter(animal => {
          return animal.id !== selectedAnimal.id;
      });
      setAnimals(filteredAnimals);  
    }
    return animalList
  };

  return (
    <div className="App" >
      <Router>
        <Switch>
          <Route
            exact path='/'
            render={()=> <Home instrumentArray={animalArray}/>}
          />
          <Route
            path='/card'
            render={()=> <Card animalArray={animalArray}/>}
          />
        </Switch>
      </Router>
      <div className="infoCont">
        <h6>© Prof. Luciano Pardo</h6>
        <h6>Colegio Américo Vespucio</h6>
      </div>
    </div>
  );
}

export default App;
