import React, {useState, useEffect} from 'react';

const Card = ({animalArray}) => {
    const [animals, setAnimals] = useState(animalArray);
    const [max2, setMax2] = useState (17);
    const [list2, setList2] = useState([]);

    const getRandomInt = (min, max) => {
        return Math.floor(Math.random() * (max - min + 1)) + min;
      }

    const getAnimals = () => {
        for(let i = 0; list2.length<9; ) {
            const num = getRandomInt(0,max2);
            setMax2(max2-1);

            const kicked = animals[num];
            const animalList = [...list2, kicked];
            setList2(animalList);

            let filteredAnimals = animals.filter(animal => {
                return animal.id !== kicked.id;
            });
            setAnimals(filteredAnimals);
        }
      };

    useEffect( () => {
        getAnimals();
    },[]);

    return (
        <div>
            <h1>Bingo</h1>
            {
                list2 && list2.map( animal => {
                    return (
                        <div className="animalCont">
                            <div>{animal.name}</div>
                            <img className="image" src={animal.picture} alt={animal.name}/>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Card;
