import React, {useState, useEffect} from 'react';

const Card = ({animalArray}) => {
    const [animals, setAnimals] = useState([]);
    const [max, setMax] = useState (17);

    const getRandomInt = (min, max) => {
        return Math.floor(Math.random() * (max - min + 1)) + min;
      }

    const getAnimals = () => {
        let nums = [];
        let cardAnimals = [];
        setMax(17);

        while (cardAnimals.length) {
            const num = getRandomInt(0,max);
            setMax(max -1);
            cardAnimals.push(animalArray[num]);
        }
        setAnimals(cardAnimals);
      };

    useEffect( () => {
        getAnimals()
    },[]);

    return (
        <div>
            <h1>Bingo</h1>
            {
                animals && animals.map( e => {
                    return (
                        <div>
                            <div>{e.name}</div>
                            <img className="image" src={e.picture} alt={e.name}/>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Card;
