import React, {useState, useEffect} from 'react';

const Card = ({animalArray}) => {
    const [animals, setAnimals] = useState([]);
    const [max2, setMax2] = useState (17);
    const [list2, setList2] = useState([]);

    const getRandomInt = (min, max) => {
        return Math.floor(Math.random() * (max - min + 1)) + min;
      }

    const getAnimals = () => {
        for(let i = 0; i<9; i++) {

        }
        const num = getRandomInt(0,max2);
        setMax2(max2 -1);

        const kickedAnimal = animalArray[num];
        const animalList = [...list2, kickedAnimal];
        setList2(animalList);

        let filteredAnimals = animalArray.filter(animal => {
            return animal.id !== kickedAnimal.id;
        });
        setAnimals(filteredAnimals);
      };

    useEffect( () => {
        getAnimals()
    },[]);

    return (
        <div>
            <h1>Bingo</h1>
            {
                animals && animals.map( e => {
                    return (
                        <div>
                            <div>{e.name}</div>
                            <img className="image" src={e.picture} alt={e.name}/>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Card;
